export function BFS(grid, start, end) {
    const visitedNodesInOrder = [];
    let stack = []
    queue.push(start);
    start.distance = 0;

    while (queue.length > 0) {
        const current = queue.shift();
        if (current.isWall) continue;
        if (current.distance === Infinity) return visitedNodesInOrder;
        if (current.isVisited) continue;

        current.isVisited = true;
        visitedNodesInOrder.push(current);
        if (current === end) return visitedNodesInOrder;

        const { col, row } = current;
        if (row > 0) {
            grid[row - 1][col].distance = Math.min(current.distance + 1, grid[row - 1][col].distance);
            grid[row - 1][col].previousNode = current;
            queue.push(grid[row - 1][col]);
        }
        if (row < grid.length - 1) {
            grid[row + 1][col].distance = Math.min(current.distance + 1, grid[row + 1][col].distance);
            grid[row + 1][col].previousNode = current;
            queue.push(grid[row + 1][col]);
        }
        if (col > 0) {
            grid[row][col - 1].distance = Math.min(current.distance + 1, grid[row][col - 1].distance);
            grid[row][col - 1].previousNode = current;
            queue.push(grid[row][col - 1]);
        }
        if (col < grid[0].length - 1) {
            grid[row][col + 1].distance = Math.min(current.distance + 1, grid[row][col + 1].distance);
            grid[row][col + 1].previousNode = current;
            queue.push(grid[row][col + 1]);
        }
    }
}

export function getNodesInShortestPathOrder_BFS(end) {
    const nodesInShortestPathOrder = [];
    let current = end;
    while (current) {
        nodesInShortestPathOrder.unshift(current);
        current = current.previousNode;
    }
    return nodesInShortestPathOrder;
}
